<?php

/**
 * NotificationSettingsTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class NotificationSettingsTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object NotificationSettingsTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('NotificationSettings');
    }
    
    /**
     * Gets all of the notification settings for a person
     * @param Integert $personId The person id
     * @return Doctrine_Collection The notification settings
     */
    public function getNotificationSettingsForPerson($personId)
    {
        $q = $this->createQuery('ns')
            ->innerJoin('ns.Notifications n')
            ->where('ns.person_id = ?', array($personId));
        return $q->execute();
    }
    
    /**
     * Gets the notification setting for a single notification and person
     * @param Integer $personId The person id
     * @param String $notificationSlug The notification slug
     * @return NotificationSettings The notification settings if one was found.
     * NULL, otherwise.
     */
    public function getNotificationSettingForPerson($personId, $notificationSlug)
    {
        $notificationSetting = NULL;
        
        // Get the notification setting
        $q = $this->createQuery('ns')
            ->innerJoin('ns.Notifications n')
            ->where('ns.person_id = ?', array($personId))
            ->andWhere('n.Slug = ?', array($notificationSlug));
        $results = $q->execute();
        
        // If a notification setting was retrieved, return it
        if ($results->count() == 1)
        {
            $notificationSetting = $results->getFirst();
        }
        return $notificationSetting;
    }
}